public class Script{	public static byte[] StartMarkPattern = new byte[] { 0xAA, 0xAA };	public static bool ValidateByChecksum = true;		public struct inData	{		public ushort startMark;		public ushort time;		public short gx;		public short gy;		public short gz;		public short ax;		public short ay;		public short az;		public short mx;		public short my;		public short mz;		public byte checksum;	};		public struct outData	{		public ushort pt_dec_startMark;		public ushort pp_pt_dec_time_plot0;		public short pp_gx_plot1;		public short pp_gy_plot2;		public short pp_gz_plot3;		public short pp_ax_plot4;		public short pp_ay_plot5;		public short pp_az_plot6;		public short pp_mx_plot7;		public short pp_my_plot8;		public short pp_mz_plot9;		public byte pt_dec_checksum;	};		public static void ProcessData(object inputObj, out object outObj)	{		// обработка данных с заполнением структуры outData		inData i = (inData)inputObj;				outObj = new outData()		{			pt_dec_startMark = i.startMark,			pp_pt_dec_time_plot0 = i.time,			pp_gx_plot1 = i.gx,			pp_gy_plot2 = i.gy,			pp_gz_plot3 = i.gz,			pp_ax_plot4 = i.ax,			pp_ay_plot5 = i.ay,			pp_az_plot6 = i.az,			pp_mx_plot7 = i.mx,			pp_my_plot8 = i.my,			pp_mz_plot9 = i.mz,			pt_dec_checksum = i.checksum		};	}}