public class Script{	public static byte[] StartMarkPattern = new byte[] { 0x24 };	public static bool ValidateByChecksum = true;		public struct inData	{		public byte startMark;		public uint time;		public short val1;		public uint val2;		public uint val3;		public uint val4;		public uint val5;		public short val6;		public uint val7;		public uint val8;		public uint val9;		public uint val10;		public short gx;		public short gy;		public short gz;		public short ax;		public short ay;		public short az;		public short val11;		public short val12;		public short val13;		public byte checksum;	};		public struct outData	{		public byte pt_dec_startMark;		public uint pp_pt_dec_time_plot0;		public short val1;		public uint val2;		public uint val3;		public uint val4;		public uint val5;		public short val6;		public uint val7;		public uint val8;		public uint val9;		public uint val10;		public float pp_gx_plot1;		public float pp_gy_plot2;		public float pp_gz_plot3;		public float pp_ax_plot4;		public float pp_ay_plot5;		public float pp_az_plot6;		public short val11;		public short val12;		public short val13;		public byte pt_hex_checksum;	};		public static void ProcessData(object inputObj, out object outObj)	{		// обработка данных с заполнением структуры outData		inData i = (inData)inputObj;				outObj = new outData()		{			pt_dec_startMark = i.startMark,			pp_pt_dec_time_plot0 = i.time,			val1 = i.val1,			val2 = i.val2,			val3 = i.val3,			val4 = i.val4,			val5 = i.val5,			val6 = i.val6,			val7 = i.val7,			val8 = i.val8,			val9 = i.val9,			val10 = i.val10,			pp_gx_plot1 = i.gx / 32768.0F * 2000.0F,			pp_gy_plot2 = i.gy / 32768.0F * 2000.0F,			pp_gz_plot3 = i.gz / 32768.0F * 2000.0F,			pp_ax_plot4 = i.ax / 32768.0F * 16.0F,			pp_ay_plot5 = i.ay / 32768.0F * 16.0F,			pp_az_plot6 = i.az / 32768.0F * 16.0F,			val11 = i.val11,			val12 = i.val12,			val13 = i.val13,			pt_hex_checksum = i.checksum		};	}}